'use strict';

var Cookie = require('express-session').Cookie;
var signature = require('cookie-signature');
var config = require('../../config');

exports = module.exports = function(req, res) {
  var workflow = new (require('events').EventEmitter)();

  workflow.outcome = {
    success: false,
    errors: [],
    errfor: {}
  };

  workflow.hasErrors = function() {
    return Object.keys(workflow.outcome.errfor).length !== 0 || workflow.outcome.errors.length !== 0;
  };

  workflow.on('exception', function(err) {
    workflow.outcome.errors.push('Exception: '+ err);
    return workflow.emit('response');
  });

  workflow.on('response', function() {
    var cookie = new Cookie(),
        val = req.sessionID,
        signed = 's:' + signature.sign(val, config.cryptoKey),
        data = cookie.serialize('connect.sid', signed);
    
    workflow.outcome.cookie = data;
    workflow.outcome.success = !workflow.hasErrors();
    res.send(workflow.outcome);
  });

  return workflow;
};